import React, { useState, useEffect } from 'react';
import { Plus, Download, Trash2, Users, FileText, RefreshCw, Lock, User, LogOut } from 'lucide-react';

const SalonApp = () => {
  // État d'authentification
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [loginError, setLoginError] = useState('');

  // États de l'application
  const [coiffeurs, setCoiffeurs] = useState(['Mouss', 'Yassine', 'Mehdi']);
  const [prestations, setPrestations] = useState([]);
  const [nouveauCoiffeur, setNouveauCoiffeur] = useState('');
  const [montrerAjoutCoiffeur, setMontrerAjoutCoiffeur] = useState(false);
  const [dateActuelle, setDateActuelle] = useState(new Date().toDateString());
  
  // État pour la prestation en cours
  const [prestationActuelle, setPrestationActuelle] = useState({
    coiffeur: '',
    prix: '',
    paiement: ''
  });

  // Clés de stockage
  const STORAGE_KEY = 'salon_data';
  const AUTH_KEY = 'salon_auth';
  const DATE_KEY = 'salon_date';

  // Vérifier l'authentification au chargement
  useEffect(() => {
    const authStatus = localStorage.getItem(AUTH_KEY);
    if (authStatus === 'authenticated') {
      setIsAuthenticated(true);
      chargerDonnees();
    }
    
    // Vérifier si c'est un nouveau jour
    verifierNouveauJour();
    
    // Configurer la vérification automatique à minuit
    const maintenant = new Date();
    const prochainMinuit = new Date(maintenant);
    prochainMinuit.setHours(24, 0, 0, 0);
    const tempsJusquMinuit = prochainMinuit.getTime() - maintenant.getTime();
    
    const timeoutMinuit = setTimeout(() => {
      verifierNouveauJour();
      // Puis vérifier chaque 24h
      setInterval(verifierNouveauJour, 24 * 60 * 60 * 1000);
    }, tempsJusquMinuit);

    return () => clearTimeout(timeoutMinuit);
  }, []);

  // Sauvegarder automatiquement les données à chaque modification
  useEffect(() => {
    if (isAuthenticated) {
      sauvegarderDonnees();
    }
  }, [prestations, coiffeurs, isAuthenticated]);

  const verifierNouveauJour = () => {
    const aujourdhui = new Date().toDateString();
    const derniereDateSauvee = localStorage.getItem(DATE_KEY);
    
    if (derniereDateSauvee && derniereDateSauvee !== aujourdhui) {
      // Nouveau jour détecté, réinitialiser les prestations
      setPrestations([]);
      localStorage.removeItem(STORAGE_KEY);
      setDateActuelle(aujourdhui);
      localStorage.setItem(DATE_KEY, aujourdhui);
      
      if (isAuthenticated) {
        alert('🌅 Nouveau jour détecté ! Les données ont été réinitialisées pour commencer une nouvelle journée.');
      }
    } else if (!derniereDateSauvee) {
      localStorage.setItem(DATE_KEY, aujourdhui);
      setDateActuelle(aujourdhui);
    }
  };

  const sauvegarderDonnees = () => {
    const donnees = {
      prestations,
      coiffeurs,
      date: dateActuelle,
      timestamp: new Date().toISOString()
    };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(donnees));
  };

  const chargerDonnees = () => {
    const donneesString = localStorage.getItem(STORAGE_KEY);
    if (donneesString) {
      try {
        const donnees = JSON.parse(donneesString);
        setPrestations(donnees.prestations || []);
        setCoiffeurs(donnees.coiffeurs || ['Mouss', 'Yassine', 'Mehdi']);
        setDateActuelle(donnees.date || new Date().toDateString());
      } catch (error) {
        console.error('Erreur lors du chargement des données:', error);
      }
    }
  };

  const actualiserManuellement = () => {
    const confirmation = window.confirm(
      '🔄 Voulez-vous actualiser l\'application ?\n\n' +
      '✅ Vos données seront conservées\n' +
      '🔄 L\'interface sera rechargée'
    );
    
    if (confirmation) {
      window.location.reload();
    }
  };

  // Gestion de l'authentification
  const handleLogin = () => {
    // Simulation d'authentification (à remplacer par votre logique)
    if (loginData.username === 'salon' && loginData.password === 'admin123') {
      setIsAuthenticated(true);
      localStorage.setItem(AUTH_KEY, 'authenticated');
      setLoginError('');
      chargerDonnees();
    } else {
      setLoginError('Nom d\'utilisateur ou mot de passe incorrect');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  const handleLogout = () => {
    const confirmation = window.confirm('Êtes-vous sûr de vouloir vous déconnecter ?');
    if (confirmation) {
      setIsAuthenticated(false);
      localStorage.removeItem(AUTH_KEY);
      setLoginData({ username: '', password: '' });
    }
  };

  // Calculs
  const totalJournee = prestations.reduce((total, p) => total + parseFloat(p.prix || 0), 0);
  const totalLiquide = prestations.filter(p => p.paiement === 'liquide').reduce((total, p) => total + parseFloat(p.prix || 0), 0);
  const totalCarte = prestations.filter(p => p.paiement === 'carte').reduce((total, p) => total + parseFloat(p.prix || 0), 0);

  const statsCoiffeurs = coiffeurs.map(coiffeur => {
    const prestationsCoiffeur = prestations.filter(p => p.coiffeur === coiffeur);
    const total = prestationsCoiffeur.reduce((sum, p) => sum + parseFloat(p.prix || 0), 0);
    return {
      nom: coiffeur,
      nbPrestations: prestationsCoiffeur.length,
      total: total
    };
  }).filter(stat => stat.nbPrestations > 0);

  const prixPredéfinis = [15, 25, 35, 45, 55, 65, 75, 85];

  const ajouterCoiffeur = () => {
    if (nouveauCoiffeur.trim()) {
      setCoiffeurs([...coiffeurs, nouveauCoiffeur.trim()]);
      setNouveauCoiffeur('');
      setMontrerAjoutCoiffeur(false);
    }
  };

  const ajouterPrestation = () => {
    if (prestationActuelle.coiffeur && prestationActuelle.prix && prestationActuelle.paiement) {
      const maintenant = new Date();
      const prestation = {
        ...prestationActuelle,
        heure: maintenant.toLocaleTimeString('fr-FR', { hour: '2-digit', minute: '2-digit' }),
        timestamp: maintenant.toISOString()
      };
      
      setPrestations([...prestations, prestation]);
      setPrestationActuelle({ coiffeur: '', prix: '', paiement: '' });
    }
  };

  const supprimerPrestation = (index) => {
    setPrestations(prestations.filter((_, i) => i !== index));
  };

  const telechargerExcel = () => {
    const today = new Date().toLocaleDateString('fr-FR');
    alert(`📥 Téléchargement lancé!\n\nFichier: Salon_${new Date().toISOString().split('T')[0]}.xlsx\nContenu: ${prestations.length} prestations\nTotal: ${totalJournee.toFixed(2)}€`);
  };

  // Page de connexion
  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-indigo-900 flex items-center justify-center p-4">
        <div className="absolute inset-0 bg-black opacity-50"></div>
        
        {/* Effets de fond animés */}
        <div className="absolute top-20 left-20 w-72 h-72 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute top-40 right-20 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute bottom-20 left-1/3 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        
        <div className="relative z-10 bg-white/10 backdrop-blur-xl rounded-3xl p-8 shadow-2xl border border-white/20 w-full max-w-md">
          <div className="text-center mb-8">
            <div className="bg-white/20 rounded-full w-20 h-20 flex items-center justify-center mx-auto mb-4">
              <Lock className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-3xl font-bold text-white mb-2">Salon de Coiffure</h1>
            <p className="text-white/80">Connexion sécurisée</p>
          </div>

          <div className="space-y-6">
            <div>
              <div className="relative">
                <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/60 w-5 h-5" />
                <input
                  type="text"
                  placeholder="Nom d'utilisateur"
                  value={loginData.username}
                  onChange={(e) => setLoginData({...loginData, username: e.target.value})}
                  onKeyPress={handleKeyPress}
                  className="w-full pl-12 pr-4 py-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-white/50 focus:border-transparent backdrop-blur-sm"
                />
              </div>
            </div>

            <div>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/60 w-5 h-5" />
                <input
                  type="password"
                  placeholder="Mot de passe"
                  value={loginData.password}
                  onChange={(e) => setLoginData({...loginData, password: e.target.value})}
                  onKeyPress={handleKeyPress}
                  className="w-full pl-12 pr-4 py-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-white/50 focus:border-transparent backdrop-blur-sm"
                />
              </div>
            </div>

            {loginError && (
              <div className="bg-red-500/20 border border-red-500/50 text-red-200 px-4 py-3 rounded-xl text-sm">
                {loginError}
              </div>
            )}

            <button
              onClick={handleLogin}
              className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-bold py-4 px-6 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg"
            >
              Se connecter
            </button>
          </div>

          <div className="mt-8 text-center">
            <div className="bg-white/10 rounded-xl p-4">
              <p className="text-white/60 text-sm mb-2">🔐 Identifiants de test :</p>
              <p className="text-white/80 text-sm">Utilisateur: <span className="font-mono bg-white/20 px-2 py-1 rounded">salon</span></p>
              <p className="text-white/80 text-sm">Mot de passe: <span className="font-mono bg-white/20 px-2 py-1 rounded">admin123</span></p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const RapportExcel = () => (
    <div className="bg-white border border-gray-300 rounded-lg overflow-hidden shadow-lg">
      <div className="bg-green-800 text-white p-4 text-center font-bold text-lg">
        📊 SALON DE COIFFURE - RAPPORT JOURNALIER
      </div>
      <div className="bg-green-100 p-3 text-center font-bold text-green-800">
        📁 Fichier: Salon_{new Date().toISOString().split('T')[0]}.xlsx
      </div>
      
      {prestations.length > 0 ? (
        <>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-blue-600 text-white">
                  <th className="p-3 text-left">N°</th>
                  <th className="p-3 text-left">Heure</th>
                  <th className="p-3 text-left">Coiffeur</th>
                  <th className="p-3 text-left">Prix (€)</th>
                  <th className="p-3 text-left">Mode de paiement</th>
                </tr>
              </thead>
              <tbody>
                {prestations.map((prestation, index) => (
                  <tr key={index} className={index % 2 === 1 ? 'bg-gray-50' : 'bg-white'}>
                    <td className="p-3">{index + 1}</td>
                    <td className="p-3">{prestation.heure}</td>
                    <td className="p-3">{prestation.coiffeur}</td>
                    <td className="p-3">{parseFloat(prestation.prix).toFixed(2)}</td>
                    <td className="p-3">
                      <span className={`font-bold ${
                        prestation.paiement === 'liquide' 
                          ? 'text-orange-600' 
                          : 'text-blue-600'
                      }`}>
                        {prestation.paiement === 'liquide' ? 'Liquide' : 'Carte'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          <div className="bg-gray-100 p-4">
            <h3 className="font-bold text-lg mb-3">📈 RÉSUMÉ DE LA JOURNÉE</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">🔢 Total prestations</div>
                <div className="text-xl text-blue-600">{prestations.length}</div>
              </div>
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">💰 Chiffre d'affaires</div>
                <div className="text-xl text-green-600">{totalJournee.toFixed(2)} €</div>
              </div>
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">💵 Liquide</div>
                <div className="text-xl text-orange-600">{totalLiquide.toFixed(2)} €</div>
              </div>
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">💳 Carte</div>
                <div className="text-xl text-blue-600">{totalCarte.toFixed(2)} €</div>
              </div>
            </div>
            
            {statsCoiffeurs.length > 0 && (
              <>
                <h4 className="font-bold mb-2">👥 DÉTAIL PAR COIFFEUR:</h4>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-2">
                  {statsCoiffeurs.map((stat, index) => (
                    <div key={index} className="bg-white p-3 rounded border">
                      <div className="font-bold">{stat.nom}</div>
                      <div className="text-sm text-gray-600">
                        {stat.nbPrestations} prestations - {stat.total.toFixed(2)} €
                      </div>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        </>
      ) : (
        <div className="p-8 text-center text-gray-500">
          <FileText className="w-16 h-16 mx-auto mb-4 text-gray-400" />
          <p>Aucune prestation enregistrée pour aujourd'hui</p>
        </div>
      )}
    </div>
  );

  return (
    <div className="max-w-6xl mx-auto p-4 bg-gray-50 min-h-screen">
      {/* En-tête avec boutons d'actions */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-600 mb-2">
              Salon de Coiffure - Suivi Journalier
            </h1>
            <p className="text-gray-600">
              {new Date().toLocaleDateString('fr-FR', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
            </p>
          </div>
          
          <div className="flex gap-2">
            <button
              onClick={actualiserManuellement}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center"
              title="Actualiser l'application (données conservées)"
            >
              <RefreshCw className="w-4 h-4 mr-2" />
              Actualiser
            </button>
            
            <button
              onClick={handleLogout}
              className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 flex items-center"
              title="Se déconnecter"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Déconnexion
            </button>
          </div>
        </div>
        
        {/* Indicateur de sauvegarde automatique */}
        <div className="bg-green-50 border border-green-200 p-3 rounded-lg mb-4">
          <div className="flex items-center text-green-700 text-sm">
            <div className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></div>
            💾 Sauvegarde automatique activée • Les données sont conservées même en cas de fermeture accidentelle
          </div>
        </div>
        
        {/* Totaux du jour */}
        <div className="grid grid-cols-3 gap-4">
          <div className="bg-green-100 p-4 rounded-lg text-center">
            <div className="text-2xl font-bold text-green-800">{totalJournee.toFixed(2)}€</div>
            <div className="text-green-600">Total Journée</div>
          </div>
          <div className="bg-blue-100 p-4 rounded-lg text-center">
            <div className="text-xl font-bold text-blue-800">{totalLiquide.toFixed(2)}€</div>
            <div className="text-blue-600">Liquide</div>
          </div>
          <div className="bg-purple-100 p-4 rounded-lg text-center">
            <div className="text-xl font-bold text-purple-800">{totalCarte.toFixed(2)}€</div>
            <div className="text-purple-600">Carte</div>
          </div>
        </div>
      </div>

      {/* Formulaire de saisie */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <Plus className="mr-2" />
          Nouvelle Prestation
        </h2>
        
        {/* Sélection du coiffeur */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Coiffeur</label>
          <div className="flex flex-wrap gap-2 mb-2">
            {coiffeurs.map((coiffeur) => (
              <button
                key={coiffeur}
                onClick={() => setPrestationActuelle({...prestationActuelle, coiffeur})}
                className={`px-4 py-2 rounded-lg font-medium ${
                  prestationActuelle.coiffeur === coiffeur
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {coiffeur}
              </button>
            ))}
          </div>
          
          {/* Bouton pour ajouter un nouveau coiffeur */}
          <button
            onClick={() => setMontrerAjoutCoiffeur(!montrerAjoutCoiffeur)}
            className="flex items-center text-blue-600 hover:text-blue-800 text-sm"
          >
            <Users className="w-4 h-4 mr-1" />
            Ajouter un nouveau coiffeur
          </button>
          
          {montrerAjoutCoiffeur && (
            <div className="flex gap-2 mt-2">
              <input
                type="text"
                value={nouveauCoiffeur}
                onChange={(e) => setNouveauCoiffeur(e.target.value)}
                placeholder="Nom du coiffeur"
                className="flex-1 p-2 border border-gray-300 rounded"
                onKeyPress={(e) => e.key === 'Enter' && ajouterCoiffeur()}
              />
              <button
                onClick={ajouterCoiffeur}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
              >
                Ajouter
              </button>
            </div>
          )}
        </div>

        {/* Prix */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Prix</label>
          <div className="flex flex-wrap gap-2 mb-2">
            {prixPredéfinis.map((prix) => (
              <button
                key={prix}
                onClick={() => setPrestationActuelle({...prestationActuelle, prix: prix.toString()})}
                className={`px-4 py-2 rounded-lg font-medium ${
                  prestationActuelle.prix === prix.toString()
                    ? 'bg-green-600 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {prix}€
              </button>
            ))}
          </div>
          <input
            type="number"
            value={prestationActuelle.prix}
            onChange={(e) => setPrestationActuelle({...prestationActuelle, prix: e.target.value})}
            placeholder="Ou saisir un montant"
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>

        {/* Mode de paiement */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Mode de paiement</label>
          <div className="flex gap-2">
            <button
              onClick={() => setPrestationActuelle({...prestationActuelle, paiement: 'liquide'})}
              className={`flex-1 py-3 rounded-lg font-medium ${
                prestationActuelle.paiement === 'liquide'
                  ? 'bg-orange-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              💵 Liquide
            </button>
            <button
              onClick={() => setPrestationActuelle({...prestationActuelle, paiement: 'carte'})}
              className={`flex-1 py-3 rounded-lg font-medium ${
                prestationActuelle.paiement === 'carte'
                  ? 'bg-indigo-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              💳 Carte
            </button>
          </div>
        </div>

        {/* Bouton d'ajout */}
        <button
          onClick={ajouterPrestation}
          disabled={!prestationActuelle.coiffeur || !prestationActuelle.prix || !prestationActuelle.paiement}
          className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
        >
          Ajouter la prestation
        </button>
      </div>

      {/* Liste des prestations */}
      {prestations.length > 0 && (
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">Prestations du jour ({prestations.length})</h2>
            <div className="flex gap-2">
              <button
                onClick={telechargerExcel}
                className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 flex items-center"
              >
                <Download className="w-4 h-4 mr-2" />
                Télécharger Excel
              </button>
            </div>
          </div>
          
          <div className="space-y-2">
            {prestations.map((prestation, index) => (
              <div key={index} className="flex items-center justify-between bg-gray-50 p-3 rounded">
                <div className="flex-1">
                  <span className="font-medium">{prestation.coiffeur}</span>
                  <span className="mx-2">•</span>
                  <span className="font-bold text-green-600">{prestation.prix}€</span>
                  <span className="mx-2">•</span>
                  <span className={`px-2 py-1 rounded text-xs ${
                    prestation.paiement === 'liquide' 
                      ? 'bg-orange-200 text-orange-800'
                      : 'bg-indigo-200 text-indigo-800'
                  }`}>
                    {prestation.paiement}
                  </span>
                  <span className="mx-2 text-gray-500">{prestation.heure}</span>
                </div>
                <button
                  onClick={() => supprimerPrestation(index)}
                  className="text-red-600 hover:text-red-800"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Rapport Excel intégré */}
      <div className="mb-6">
        <div className="bg-yellow-100 border border-yellow-300 p-3 rounded-lg mb-4">
          <div className="flex items-center">
            <FileText className="w-5 h-5 text-yellow-600 mr-2" />
            <span className="font-medium text-yellow-800">
              💾 Fichier Excel - Aperçu en temps réel
            </span>
          </div>
          <div className="text-sm text-yellow-700 mt-1">
            Ce rapport sera automatiquement sauvegardé • Compatible Excel, LibreOffice, Google Sheets
          </div>
        </div>
        <RapportExcel />
      </div>
    </div>
  );
};

export default SalonApp;
