import React, { useState, useEffect } from 'react';
import { Plus, Download, Trash2, Users, FileText, LogOut, RefreshCw, X, Trash } from 'lucide-react';

const SalonApp = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  
  // Ã‰tats avec chargement depuis une simulation de localStorage
  const [coiffeurs, setCoiffeurs] = useState([]);
  const [prestations, setPrestations] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  
  const [nouveauCoiffeur, setNouveauCoiffeur] = useState('');
  const [montrerAjoutCoiffeur, setMontrerAjoutCoiffeur] = useState(false);
  
  // Ã‰tat pour la prestation en cours
  const [prestationActuelle, setPrestationActuelle] = useState({
    coiffeur: '',
    prix: '',
    paiement: ''
  });  

  // Charger les donnÃ©es au dÃ©marrage depuis le storage global
  useEffect(() => {
    if (!dataLoaded) {
      const savedCoiffeurs = JSON.parse(localStorage.getItem("coiffeurs")) || [];
      const savedPrestations = JSON.parse(localStorage.getItem("prestations")) || [];
      const savedIsLoggedIn = JSON.parse(localStorage.getItem("isLoggedIn")) || false;

      setCoiffeurs(savedCoiffeurs);
      setPrestations(savedPrestations);
      setIsLoggedIn(savedIsLoggedIn);
      setDataLoaded(true);
    }
  }, [dataLoaded]);

  // Sauvegarder automatiquement dans le storage global
  useEffect(() => {
    if (dataLoaded) {
      localStorage.setItem("coiffeurs", JSON.stringify(coiffeurs));
      localStorage.setItem("prestations", JSON.stringify(prestations));
      localStorage.setItem("isLoggedIn", JSON.stringify(isLoggedIn));
    }
  }, [coiffeurs, prestations, isLoggedIn, dataLoaded]);

  // Calcul du total journalier
  const totalJournee = prestations.reduce((total, p) => total + parseFloat(p.prix || 0), 0);
  const totalLiquide = prestations.filter(p => p.paiement === 'liquide').reduce((total, p) => total + parseFloat(p.prix || 0), 0);
  const totalCarte = prestations.filter(p => p.paiement === 'carte').reduce((total, p) => total + parseFloat(p.prix || 0), 0);

  // Statistiques par coiffeur
  const statsCoiffeurs = coiffeurs.map(coiffeur => {
    const prestationsCoiffeur = prestations.filter(p => p.coiffeur === coiffeur);
    const total = prestationsCoiffeur.reduce((sum, p) => sum + parseFloat(p.prix || 0), 0);
    return {
      nom: coiffeur,
      nbPrestations: prestationsCoiffeur.length,
      total: total
    };
  }).filter(stat => stat.nbPrestations > 0);

  // Prix prÃ©dÃ©finis pour faciliter la saisie
  const prixPredÃ©finis = [10, 15, 20, 25, 35, 50, 70, 90];

  // Fonctions de connexion
  const handleLogin = () => {
    if (loginData.username && loginData.password) {
      setIsLoggedIn(true);
    } else {
      alert('Veuillez remplir tous les champs');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setLoginData({ username: '', password: '' });
  };

  // Fonction d'actualisation MODIFIÃ‰E - Ne touche PAS aux coiffeurs
  const actualiserDonnees = () => {
    if (prestations.length > 0) {
      // TÃ©lÃ©charger avant d'actualiser
      telechargerExcel();
      
      // Petit dÃ©lai pour permettre le tÃ©lÃ©chargement
      setTimeout(() => {
        if (window.confirm('Voulez-vous vraiment actualiser les donnÃ©es ? Seules les prestations seront effacÃ©es.\n\nâš ï¸ Les coiffeurs seront conservÃ©s.')) {
          // SEULEMENT actualiser les prestations, PAS les coiffeurs
          setPrestations([]);
          setPrestationActuelle({ coiffeur: '', prix: '', paiement: '' });
          
          // Mettre Ã  jour le storage global - GARDER les coiffeurs
          
          
          alert('âœ… Prestations actualisÃ©es et fichier tÃ©lÃ©chargÃ© !\n\nðŸ‘¥ Les coiffeurs ont Ã©tÃ© conservÃ©s.');
        }
      }, 500);
    } else {
      if (window.confirm('Aucune prestation Ã  sauvegarder. Actualiser quand mÃªme ?')) {
        setPrestations([]);
        setPrestationActuelle({ coiffeur: '', prix: '', paiement: '' });
        
        // Mettre Ã  jour seulement les prestations dans le storage global
       
        
        alert('âœ… Prestations actualisÃ©es !\n\nðŸ‘¥ Les coiffeurs ont Ã©tÃ© conservÃ©s.');
      }
    }
  };

  const ajouterCoiffeur = () => {
    if (nouveauCoiffeur.trim()) {
      const nouveauxCoiffeurs = [...coiffeurs, nouveauCoiffeur.trim()];
      setCoiffeurs(nouveauxCoiffeurs);
      
      // FORCER la sauvegarde immÃ©diate des coiffeurs dans le storage global
      
      setNouveauCoiffeur('');
      setMontrerAjoutCoiffeur(false);
    }
  };

  const supprimerCoiffeur = (coiffeurASupprimer) => {
    if (window.confirm(`ÃŠtes-vous sÃ»r de vouloir supprimer "${coiffeurASupprimer}" ? Cette action est irrÃ©versible.`)) {
      // Supprimer le coiffeur de la liste
      const nouveauxCoiffeurs = coiffeurs.filter(c => c !== coiffeurASupprimer);
      setCoiffeurs(nouveauxCoiffeurs);
      
      // FORCER la sauvegarde immÃ©diate des coiffeurs dans le storage global
  
      
      // Si c'est le coiffeur sÃ©lectionnÃ© actuellement, le dÃ©sÃ©lectionner
      if (prestationActuelle.coiffeur === coiffeurASupprimer) {
        setPrestationActuelle({...prestationActuelle, coiffeur: ''});
      }
    }
  };

  const annulerAjoutCoiffeur = () => {
    setMontrerAjoutCoiffeur(false);
    setNouveauCoiffeur('');
  };

  const ajouterPrestation = () => {
    if (prestationActuelle.coiffeur && prestationActuelle.prix && prestationActuelle.paiement) {
      const maintenant = new Date();
      const prestation = {
        ...prestationActuelle,
        heure: maintenant.toLocaleTimeString('fr-FR', { hour: '2-digit', minute: '2-digit' }),
        timestamp: maintenant.toISOString()
      };
      
      setPrestations([...prestations, prestation]);
      setPrestationActuelle({ coiffeur: '', prix: '', paiement: '' });
    }
  };

  // NOUVEAU : Fonction pour valider la saisie du prix
  const validerSaisiePrix = () => {
    if (prestationActuelle.prix && prestationActuelle.prix.trim() !== '') {
      // Si tous les champs sont remplis, ajouter directement la prestation
      if (prestationActuelle.coiffeur && prestationActuelle.paiement) {
        ajouterPrestation();
      }
      // Sinon, juste valider le prix (garder le focus sur les autres champs)
    }
  };

  const supprimerPrestation = (index) => {
    setPrestations(prestations.filter((_, i) => i !== index));
  };

  // Fonction pour gÃ©nÃ©rer le contenu CSV (compatible Excel)
  const genererCSV = () => {
    const headers = ['NÂ°', 'Heure', 'Coiffeur', 'Prix (â‚¬)', 'Mode de paiement'];
    
    let csvContent = '\uFEFF'; // BOM pour UTF-8
    csvContent += `SALON DE COIFFURE - RAPPORT JOURNALIER\n`;
    csvContent += `Date: ${new Date().toLocaleDateString('fr-FR')}\n`;
    csvContent += `\n`;
    csvContent += headers.join(';') + '\n';
    
    prestations.forEach((prestation, index) => {
      const row = [
        index + 1,
        prestation.heure,
        prestation.coiffeur,
        parseFloat(prestation.prix).toFixed(2),
        prestation.paiement === 'liquide' ? 'Liquide' : 'Carte'
      ];
      csvContent += row.join(';') + '\n';
    });
    
    // Ajouter les totaux
    csvContent += '\n';
    csvContent += 'RÃ‰SUMÃ‰ DE LA JOURNÃ‰E\n';
    csvContent += `Total prestations;${prestations.length}\n`;
    csvContent += `Chiffre d'affaires;${totalJournee.toFixed(2)}â‚¬\n`;
    csvContent += `Paiement Liquide;${totalLiquide.toFixed(2)}â‚¬\n`;
    csvContent += `Paiement Carte;${totalCarte.toFixed(2)}â‚¬\n`;
    
    // DÃ©tail par coiffeur
    if (statsCoiffeurs.length > 0) {
      csvContent += '\n';
      csvContent += 'DÃ‰TAIL PAR COIFFEUR\n';
      csvContent += 'Nom;Prestations;Total (â‚¬)\n';
      statsCoiffeurs.forEach(stat => {
        csvContent += `${stat.nom};${stat.nbPrestations};${stat.total.toFixed(2)}\n`;
      });
    }
    
    return csvContent;
  };

  const telechargerExcel = () => {
    try {
      const csvContent = genererCSV();
      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');
      
      if (link.download !== undefined) {
        const url = URL.createObjectURL(blob);
        link.setAttribute('href', url);
        link.setAttribute('download', `Salon_${new Date().toISOString().split('T')[0]}.csv`);
        link.style.visibility = 'hidden';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        
        setTimeout(() => {
          setCoiffeurs([...coiffeurs]);
          setPrestations([...prestations]);
          
          alert(`âœ… Fichier tÃ©lÃ©chargÃ© avec succÃ¨s!\n\nðŸ“ ${prestations.length} prestations exportÃ©es\nðŸ’° Total: ${totalJournee.toFixed(2)}â‚¬\n\nðŸ“Š Les donnÃ©es ont Ã©tÃ© actualisÃ©es`);
        }, 100);
      }
    } catch (error) {
      console.error('Erreur lors du tÃ©lÃ©chargement:', error);
      alert('âŒ Erreur lors du tÃ©lÃ©chargement du fichier');
    }
  };

  // Composant de connexion
  const LoginPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-blue-700 to-purple-800 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md p-8 transform transition-all">
        <div className="text-center mb-8">
          <div className="w-20 h-20 bg-gradient-to-r from-blue-600 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-4">
            <Users className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Salon de Coiffure</h1>
          <p className="text-gray-600">Connexion au systÃ¨me de gestion</p>
        </div>
        
        <div className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Nom d'utilisateur
            </label>
            <input
              type="text"
              value={loginData.username}
              onChange={(e) => setLoginData({...loginData, username: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
              placeholder="Entrez votre nom d'utilisateur"
              onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Mot de passe
            </label>
            <input
              type="password"
              value={loginData.password}
              onChange={(e) => setLoginData({...loginData, password: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
              placeholder="Entrez votre mot de passe"
              onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
            />
          </div>
          
          <button
            onClick={handleLogin}
            className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-purple-700 transform hover:scale-105 transition-all duration-200 shadow-lg"
          >
            Se connecter
          </button>
          
          <div className="text-center text-sm text-gray-500">
            Application de gestion pour salon de coiffure
          </div>
        </div>
      </div>
    </div>
  );

  const RapportExcel = () => (
    <div className="bg-white border border-gray-300 rounded-lg overflow-hidden shadow-lg">
      <div className="bg-green-800 text-white p-4 text-center font-bold text-lg">
        ðŸ“Š SALON DE COIFFURE - RAPPORT JOURNALIER
      </div>
      <div className="bg-green-100 p-3 text-center font-bold text-green-800">
        ðŸ“ Fichier: Salon_{new Date().toISOString().split('T')[0]}.csv
      </div>
      
      {prestations.length > 0 ? (
        <>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-blue-600 text-white">
                  <th className="p-3 text-left">NÂ°</th>
                  <th className="p-3 text-left">Heure</th>
                  <th className="p-3 text-left">Coiffeur</th>
                  <th className="p-3 text-left">Prix (â‚¬)</th>
                  <th className="p-3 text-left">Mode de paiement</th>
                </tr>
              </thead>
              <tbody>
                {prestations.map((prestation, index) => (
                  <tr key={index} className={index % 2 === 1 ? 'bg-gray-50' : 'bg-white'}>
                    <td className="p-3">{index + 1}</td>
                    <td className="p-3">{prestation.heure}</td>
                    <td className="p-3">{prestation.coiffeur}</td>
                    <td className="p-3">{parseFloat(prestation.prix).toFixed(2)}</td>
                    <td className="p-3">
                      <span className={`font-bold ${
                        prestation.paiement === 'liquide' 
                          ? 'text-orange-600' 
                          : 'text-blue-600'
                      }`}>
                        {prestation.paiement === 'liquide' ? 'Liquide' : 'Carte'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          <div className="bg-gray-100 p-4">
            <h3 className="font-bold text-lg mb-3">ðŸ“ˆ RÃ‰SUMÃ‰ DE LA JOURNÃ‰E</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">ðŸ”¢ Total prestations</div>
                <div className="text-xl text-blue-600">{prestations.length}</div>
              </div>
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">ðŸ’° Chiffre d'affaires</div>
                <div className="text-xl text-green-600">{totalJournee.toFixed(2)} â‚¬</div>
              </div>
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">ðŸ’µ Liquide</div>
                <div className="text-xl text-orange-600">{totalLiquide.toFixed(2)} â‚¬</div>
              </div>
              <div className="bg-white p-3 rounded border">
                <div className="font-bold">ðŸ’³ Carte</div>
                <div className="text-xl text-blue-600">{totalCarte.toFixed(2)} â‚¬</div>
              </div>
            </div>
            
            {statsCoiffeurs.length > 0 && (
              <>
                <h4 className="font-bold mb-2">ðŸ‘¥ DÃ‰TAIL PAR COIFFEUR:</h4>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-2">
                  {statsCoiffeurs.map((stat, index) => (
                    <div key={index} className="bg-white p-3 rounded border">
                      <div className="font-bold">{stat.nom}</div>
                      <div className="text-sm text-gray-600">
                        {stat.nbPrestations} prestations - {stat.total.toFixed(2)} â‚¬
                      </div>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        </>
      ) : (
        <div className="p-8 text-center text-gray-500">
          <FileText className="w-16 h-16 mx-auto mb-4 text-gray-400" />
          <p>Aucune prestation enregistrÃ©e pour aujourd'hui</p>
        </div>
      )}
    </div>
  );

  if (!dataLoaded) {
    return (
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh"
      }}>
        <div style={{
          border: "4px solid rgba(0, 0, 0, 0.1)",
          borderLeftColor: "#4f46e5", // violet
          borderRadius: "50%",
          width: "40px",
          height: "40px",
          animation: "spin 1s linear infinite"
        }}></div>
        <style>
          {`@keyframes spin { to { transform: rotate(360deg); } }`}
        </style>
      </div>
    );
  }


  // Si pas connectÃ©, afficher la page de connexion
  if (!isLoggedIn) {
    return <LoginPage />;
  }
  

  return (
    <div className="max-w-6xl mx-auto p-4 bg-gray-50 min-h-screen">
      {/* En-tÃªte avec boutons de contrÃ´le */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-xl sm:text-2xl font-bold text-blue-600 mb-2">
              Salon de Coiffure - Suivi Journalier
            </h1>
            <p className="text-gray-600">
              {new Date().toLocaleDateString('fr-FR', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
            </p>
          </div>
          
          <div className="flex gap-1 flex-col sm:flex-row">
            <button
              onClick={actualiserDonnees}
              className="bg-orange-600 text-white px-2 sm:px-4 py-2 rounded-lg hover:bg-orange-700 flex items-center transition-colors text-sm sm:text-base"
              title="Actualiser les prestations (garde les coiffeurs)"
            >
              <RefreshCw className="w-4 h-4 mr-2" />
              Actualiser
            </button>
            <button
              onClick={handleLogout}
              className="bg-red-600 text-white px-2 sm:px-4 py-2 rounded-lg hover:bg-red-700 flex items-center transition-colors text-sm sm:text-base"
            >
              <LogOut className="w-4 h-4 mr-2" />
              DÃ©connexion
            </button>
          </div>
        </div>
        
        {/* Totaux du jour */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          <div className="bg-green-100 p-4 rounded-lg text-center">
            <div className="text-lg sm:text-2xl font-bold text-green-800">{totalJournee.toFixed(2)}â‚¬</div>
            <div className="text-green-600">Total JournÃ©e</div>
          </div>
          <div className="bg-blue-100 p-4 rounded-lg text-center">
            <div className="text-base sm:text-xl font-bold text-blue-800">{totalLiquide.toFixed(2)}â‚¬</div>
            <div className="text-blue-600">Liquide</div>
          </div>
          <div className="bg-purple-100 p-4 rounded-lg text-center">
            <div className="text-xl font-bold text-purple-800">{totalCarte.toFixed(2)}â‚¬</div>
            <div className="text-purple-600">Carte</div>
          </div>
        </div>
      </div>

      {/* Formulaire de saisie */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <Plus className="mr-2" />
          Nouvelle Prestation
        </h2>
        
        {/* Gestion des coiffeurs */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Coiffeurs</label>
          
          {/* Liste des coiffeurs existants */}
          {coiffeurs.length > 0 ? (
            <div className="flex flex-wrap gap-2 mb-3">
              {coiffeurs.map((coiffeur) => (
                <div key={coiffeur} className="flex items-center">
                  <button
                    onClick={() => setPrestationActuelle({...prestationActuelle, coiffeur})}
                    className={`px-4 py-2 rounded-lg font-medium ${
                      prestationActuelle.coiffeur === coiffeur
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                    } transition-colors`}
                  >
                    {coiffeur}
                  </button>
                  <button
                    onClick={() => supprimerCoiffeur(coiffeur)}
                    className="ml-1 text-red-600 hover:text-red-800 p-1"
                    title="Supprimer ce coiffeur"
                  >
                    <Trash className="w-4 h-4" />
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <div className="bg-yellow-100 border border-yellow-300 p-3 rounded-lg mb-3">
              <p className="text-yellow-800">Aucun coiffeur enregistrÃ©. Ajoutez-en un pour commencer.</p>
            </div>
          )}
          
          {/* Formulaire d'ajout de coiffeur */}
          {!montrerAjoutCoiffeur ? (
            <button
              onClick={() => setMontrerAjoutCoiffeur(true)}
              className="flex items-center text-blue-600 hover:text-blue-800 text-sm bg-blue-50 px-3 py-2 rounded-lg hover:bg-blue-100 transition-colors"
            >
              <Plus className="w-4 h-4 mr-1" />
              Ajouter un nouveau coiffeur
            </button>
          ) : (
            <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
              <div className="flex items-center justify-between mb-2">
                <h4 className="font-medium text-blue-800">Nouveau coiffeur</h4>
                <button
                  onClick={annulerAjoutCoiffeur}
                  className="text-gray-500 hover:text-gray-700"
                  title="Annuler"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
              <div className="flex gap-2">
                <input
                  type="text"
                  value={nouveauCoiffeur}
                  onChange={(e) => setNouveauCoiffeur(e.target.value)}
                  placeholder="Nom du coiffeur"
                  className="flex-1 p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  onKeyPress={(e) => e.key === 'Enter' && ajouterCoiffeur()}
                  autoFocus
                />
                <button
                  onClick={ajouterCoiffeur}
                  disabled={!nouveauCoiffeur.trim()}
                  className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                >
                  Ajouter
                </button>
                <button
                  onClick={annulerAjoutCoiffeur}
                  className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-colors"
                >
                  Annuler
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Prix */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Prix</label>
          <div className="flex flex-wrap gap-2 mb-2">
            {prixPredÃ©finis.map((prix) => (
              <button
                key={prix}
                onClick={() => setPrestationActuelle({...prestationActuelle, prix: prix.toString()})}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  prestationActuelle.prix === prix.toString()
                    ? 'bg-green-600 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {prix}â‚¬
              </button>
            ))}
          </div>
          {/* MODIFICATION : Suppression du bouton flÃ¨che, garde juste l'input */} 
          <input
            type="number"
            value={prestationActuelle.prix}
            onChange={(e) => setPrestationActuelle({...prestationActuelle, prix: e.target.value})}
            placeholder="Ou saisir un montant"
            className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
            onKeyPress={(e) => e.key === 'Enter' && validerSaisiePrix()}
          />
        </div>
        

        {/* Mode de paiement */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">Mode de paiement</label>
          <div className="flex gap-2">
            <button
              onClick={() => setPrestationActuelle({...prestationActuelle, paiement: 'liquide'})}
              className={`flex-1 py-3 rounded-lg font-medium transition-colors ${
                prestationActuelle.paiement === 'liquide'
                  ? 'bg-orange-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              ðŸ’µ Liquide
            </button>
            <button
              onClick={() => setPrestationActuelle({...prestationActuelle, paiement: 'carte'})}
              className={`flex-1 py-3 rounded-lg font-medium transition-colors ${
                prestationActuelle.paiement === 'carte'
                  ? 'bg-indigo-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              ðŸ’³ Carte
            </button>
          </div>
        </div>

        {/* Bouton d'ajout */}
        <button
          onClick={ajouterPrestation}
          disabled={!prestationActuelle.coiffeur || !prestationActuelle.prix || !prestationActuelle.paiement}
          className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
        >
          Ajouter la prestation
        </button>
      </div>

      {/* Liste des prestations */}
      {prestations.length > 0 && (
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">Prestations du jour ({prestations.length})</h2>
            <div className="flex gap-2">
              <button
                onClick={telechargerExcel}
                className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 flex items-center transition-colors"
              >
                <Download className="w-4 h-4 mr-2" />
                TÃ©lÃ©charger Excel
              </button>
            </div>
          </div>
          
          <div className="space-y-2">
            {prestations.map((prestation, index) => (
              <div key={index} className="flex items-center justify-between bg-gray-50 p-3 rounded-lg">
                <div className="flex-1">
                  <span className="font-medium">{prestation.coiffeur}</span>
                  <span className="mx-2">â€¢</span>
                  <span className="font-bold text-green-600">{prestation.prix}â‚¬</span>
                  <span className="mx-2">â€¢</span>
                  <span className={`px-2 py-1 rounded text-xs ${
                    prestation.paiement === 'liquide' 
                      ? 'bg-orange-200 text-orange-800'
                      : 'bg-indigo-200 text-indigo-800'
                  }`}>
                    {prestation.paiement}
                  </span>
                  <span className="mx-2 text-gray-500">{prestation.heure}</span>
                </div>
                <button
                  onClick={() => supprimerPrestation(index)}
                  className="text-red-600 hover:text-red-800 transition-colors"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Rapport Excel intÃ©grÃ© - TOUJOURS VISIBLE */}
      <div className="mb-6">
        <div className="bg-yellow-100 border border-yellow-300 p-3 rounded-lg mb-4">
          <div className="flex items-center">
            <FileText className="w-5 h-5 text-yellow-600 mr-2" />
            <span className="font-medium text-yellow-800">
              ðŸ’¾ Fichier CSV/Excel - AperÃ§u en temps rÃ©el
            </span>
          </div>
          <div className="text-sm text-yellow-700 mt-1">
            Format CSV compatible avec Excel, LibreOffice, Google Sheets â€¢ TÃ©lÃ©chargement direct
          </div>
        </div>
        <RapportExcel />
      </div>
    </div>
  );
};

export default SalonApp;
