                  setRecherchePrestation(e.target.value);
                  setSuggestionsVisible(true);
                  if (!e.target.value) {
                    setPrestationActuelle(prev => ({...prev, prestation: ''}));
                  }
                }}
                onFocus={() => setSuggestionsVisible(true)}
                placeholder={`Rechercher une prestation ${categorieSelectionnee}...`}
