import React, { useState } from "react";
import { Users, UserPlus } from "lucide-react";
import SalonApp from "./SalonApp";

// Composant de connexion
const LoginPage = () => {
  // √âtats
  const [showCreateAccount, setShowCreateAccount] = useState(false);
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [createAccountData, setCreateAccountData] = useState({
    salonName: "",
    username: "",
    password: "",
    confirmPassword: "",
  });

  // Gestion de la connexion
  const handleLogin = () => {
    console.log("Tentative de connexion :", loginData);
    // üëâ Ici tu pourras appeler ton API ou v√©rifier les identifiants
    alert(`Connexion avec ${loginData.username}`);
  };

  // Gestion de la cr√©ation de compte
  const handleCreateAccount = () => {
    if (createAccountData.password !== createAccountData.confirmPassword) {
      alert("‚ö†Ô∏è Les mots de passe ne correspondent pas !");
      return;
    }
    console.log("Cr√©ation de compte :", createAccountData);
    alert(`Compte cr√©√© pour ${createAccountData.username}`);
    setShowCreateAccount(false); // Retour vers connexion apr√®s cr√©ation
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-blue-700 to-purple-800 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md p-8 transform transition-all">
        <div className="text-center mb-8">
          <div className="w-20 h-20 bg-gradient-to-r from-blue-600 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-4">
            <Users className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">
            Salon de Coiffure
          </h1>
          <p className="text-gray-600">
            {showCreateAccount
              ? "Cr√©er un nouveau compte"
              : "Connexion au syst√®me de gestion"}
          </p>
        </div>

        {!showCreateAccount ? (
          // Formulaire de connexion
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Nom d'utilisateur
              </label>
              <input
                type="text"
                value={loginData.username}
                onChange={(e) =>
                  setLoginData({ ...loginData, username: e.target.value })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                placeholder="Entrez votre nom d'utilisateur"
                onKeyPress={(e) => e.key === "Enter" && handleLogin()}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Mot de passe
              </label>
              <input
                type="password"
                value={loginData.password}
                onChange={(e) =>
                  setLoginData({ ...loginData, password: e.target.value })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                placeholder="Entrez votre mot de passe"
                onKeyPress={(e) => e.key === "Enter" && handleLogin()}
              />
            </div>

            <button
              onClick={handleLogin}
              className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-purple-700 transform hover:scale-105 transition-all duration-200 shadow-lg"
            >
              Se connecter
            </button>

            <div className="text-center">
              <button
                onClick={() => setShowCreateAccount(true)}
                className="text-blue-600 hover:text-blue-800 text-sm font-medium flex items-center justify-center mx-auto"
              >
                <UserPlus className="w-4 h-4 mr-2" />
                Pas encore de compte ? Cr√©er un compte
              </button>
            </div>

            <div className="text-center text-sm text-gray-500">
              Application de gestion pour salon de coiffure
            </div>
          </div>
        ) : (
          // Formulaire de cr√©ation de compte
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Nom du salon
              </label>
              <input
                type="text"
                value={createAccountData.salonName}
                onChange={(e) =>
                  setCreateAccountData({
                    ...createAccountData,
                    salonName: e.target.value,
                  })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
                placeholder="Ex: Salon Belle Coupe"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Nom d'utilisateur
              </label>
              <input
                type="text"
                value={createAccountData.username}
                onChange={(e) =>
                  setCreateAccountData({
                    ...createAccountData,
                    username: e.target.value,
                  })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
                placeholder="Choisissez un nom d'utilisateur"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Mot de passe
              </label>
              <input
                type="password"
                value={createAccountData.password}
                onChange={(e) =>
                  setCreateAccountData({
                    ...createAccountData,
                    password: e.target.value,
                  })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
                placeholder="Minimum 4 caract√®res"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Confirmer le mot de passe
              </label>
              <input
                type="password"
                value={createAccountData.confirmPassword}
                onChange={(e) =>
                  setCreateAccountData({
                    ...createAccountData,
                    confirmPassword: e.target.value,
                  })
                }
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
                placeholder="Confirmez votre mot de passe"
                onKeyPress={(e) => e.key === "Enter" && handleCreateAccount()}
              />
            </div>

            <button
              onClick={handleCreateAccount}
              className="w-full bg-gradient-to-r from-green-600 to-green-700 text-white py-3 rounded-lg font-semibold hover:from-green-700 hover:to-green-800 transform hover:scale-105 transition-all duration-200 shadow-lg"
            >
              Cr√©er le compte
            </button>

            <div className="text-center">
              <button
                onClick={() => setShowCreateAccount(false)}
                className="text-gray-600 hover:text-gray-800 text-sm font-medium"
              >
                ‚Üê Retour √† la connexion
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LoginPage;
